      this.setState({repos: array.data })
    // });

    // this.setState({ repos: newdata });
  });
}

  getData = () => {
    axios.get(`http://localhost:9000/data`).then(array => {
      let data = array.data;
      this.setState({ repos: data });
    });
  };

  // del = id => {
